import 'package:speed_test_dart/classes/classes.dart';
import 'package:speed_test_dart/speed_test_dart.dart';

class SpeedTestService {
  final SpeedTestDart _tester = SpeedTestDart();
  List<Server> _bestServersList = [];

  Future<void> setBestServers() async {
    final settings = await _tester.getSettings();
    final servers = settings.servers;
    _bestServersList = await _tester.getBestServers(servers: servers);
  }

  Future<double> testDownloadSpeed() async {
    return await _tester.testDownloadSpeed(servers: _bestServersList);
  }

  Future<double> testUploadSpeed() async {
    return await _tester.testUploadSpeed(servers: _bestServersList);
  }

  bool get isReadyToTest => _bestServersList.isNotEmpty;
}

