    await _speedTestService.setBestServers();
    setState(() {
      readyToTest = _speedTestService.isReadyToTest;
    });
  }

  Future<void> _testDownloadSpeed() async {
    setState(() {
      loadingDownload = true;
    });
    final _downloadRate = await _speedTestService.testDownloadSpeed();
    setState(() {
      downloadRate = _downloadRate;
      _speedValue = downloadRate;
      loadingDownload = false;
    });
  }

  Future<void> _testUploadSpeed() async {
    setState(() {
      loadingUpload = true;
    });
    final _uploadRate = await _speedTestService.testUploadSpeed();
    setState(() {
      uploadRate = _uploadRate;
      _speedValue = uploadRate;
      loadingUpload = false;
    });
  }

