import 'dart:io';
import 'dart:typed_data';


import 'package:just_audio/just_audio.dart' as just_audio;
import 'package:audioplayers/audioplayers.dart' as audioplayers;
import 'package:flutter/material.dart';
import 'package:path_provider/path_provider.dart';
import 'morse_code_utils.dart';
import 'audio_generator.dart';

class MorseCodeConverter extends StatefulWidget {
  @override
  _MorseCodeConverterState createState() => _MorseCodeConverterState();
}

class _MorseCodeConverterState extends State<MorseCodeConverter> {
  final TextEditingController _textController = TextEditingController();
  double _frequency = 700;
  double _wpm = 30;
  String _audioFilePath = '';

  List<String> _audioFiles = [];


  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Morse Code Audio Converter'),
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: Column(
          children: [
            TextField(
              controller: _textController,
              decoration: InputDecoration(labelText: 'Enter text'),
            ),
            SizedBox(height: 20),
            Text('Frequency: ${_frequency.round()} Hz'),
            Slider(
              value: _frequency,
              min: 400,
              max: 900,
              onChanged: (value) {
                setState(() {
                  _frequency = value;
                });
              },
            ),
            SizedBox(height: 20),
            Text('Speed: ${_wpm.round()} WPM'),
            Slider(
              value: _wpm,
              min: 10,
              max: 40,
              onChanged: (value) {
                setState(() {
                  _wpm = value;
                });
              },
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: () async {
                await _convertToMorse();
                ScaffoldMessenger.of(context).showSnackBar(
                  SnackBar(content: Text('Morse code audio generated')),
                );
              },
              child: Text('Convert'),
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: _audioFilePath.isNotEmpty ? _playAudio : null,
              child: Text('Play'),
            ),
            SizedBox(height: 20),
            Expanded(
              child: ListView.builder(
                itemCount: _audioFiles.length,
                itemBuilder: (context, index) {
                  return ListTile(
                    title: Text(File(_audioFiles[index]).uri.pathSegments.last),
                    onTap: () async {
                      final player = just_audio.AudioPlayer();
                      await player.play(DeviceFileSource(_audioFiles[index]));
                    },
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }

  Future<void> _convertToMorse() async {
    String morseCode = MorseCodeUtils.textToMorse(_textController.text);
    Uint8List audio = await AudioGenerator.generateMorseAudio(morseCode, _frequency, _wpm);
    
    final directory = await getApplicationDocumentsDirectory();
    final fileName = '${_textController.text.replaceAll(RegExp(r'\s+'), '').substring(0, _textController.text.replaceAll(RegExp(r'\s+'), '').length.clamp(0, 15))}_${DateTime.now().millisecondsSinceEpoch % 1000000}.wav';
    final path = '${directory.path}/$fileName';

    
    await AudioGenerator.saveAsWav(
      path: path,
      data: audio
    );

    setState(() {
      _audioFilePath = path;
    });

    await _updateAudioFilesList();
  }

  Future<void> _playAudio() async {
    if (_audioFilePath.isNotEmpty) {
      final player = just_audio.AudioPlayer();
      await player.setFilePath(_audioFilePath);
      await player.play();
    }
  }

  Future<void> _updateAudioFilesList() async {
  final directory = await getApplicationDocumentsDirectory();
  final files = Directory(directory.path)
      .listSync()
      .where((file) => file.path.endsWith('.wav'))
      .map((file) => file.path)
      .toList();
  files.sort((a, b) => File(b).lastModifiedSync().compareTo(File(a).lastModifiedSync()));
  setState(() {
    _audioFiles = files;
  });
}

}

